mapper.insert:
insert into cpprkdb(djlsh, djbth, scpch, cpbh, cpmc, cpxl, cpdj, gg)
select #{purchaseID},ifnull((max(djbth)+1),1),#{lot},#{productID},#{productName},#{productType},'Açº§',#{gl}
from cpprkdb
where djlsh=#{purchaseID}

mapper.delete:
delete
from cpprkdb
where djlsh = #{purchaseID} and djbth=#{documentsTbodyID};

